// 1 -- 12345; 2 -- ii; 3 -- (iv)
int isnum(char *word) {
  int ret = -1;
  if (word != NULL) {
    int ii;
    int no_good = 0;
    static char aa[100],bb[100],cc[100];
    for (ii = 0; no_good == 0 && ii < strlen(word); ii++) {
      if (isdigit(word[ii]) == 0) no_good = 1;
    }
    if (no_good == 0) {
      ret = 1;
    } else if (sscanf(word,"%[ivx]",aa) == 1)  {
      ret = 2;
    } else if (sscanf(word,"%[(]%[ivx]%[)]",aa,bb,cc) == 3)  {
      ret = 3;
    } else {
      ret = 0;
    }
  }
  return ret;
}

